import{_ as t,c as o,j as e,a as r,G as l,a2 as h,B as n,o as d}from"./chunks/framework.DPuwY6B9.js";const s="/shots/docs_aera_discribtion.png",x=JSON.parse('{"title":"页面设置","description":"","frontmatter":{"title":"页面设置"},"headers":[],"relativePath":"Sakurairo/Pages/index.md","filePath":"Sakurairo/Pages/index.md"}'),c={name:"Sakurairo/Pages/index.md"},p={id:"页面设置",tabindex:"-1"};function u(b,a,m,q,k,P){const i=n("Badge");return d(),o("div",null,[e("h1",p,[a[0]||(a[0]=r("页面设置 ")),l(i,{type:"tip",text:"v3.0"}),a[1]||(a[1]=r()),a[2]||(a[2]=e("a",{class:"header-anchor",href:"#页面设置","aria-label":'Permalink to "页面设置 <Badge type="tip" text="v3.0" />"'},"​",-1))]),a[3]||(a[3]=h('<h2 id="综合设置" tabindex="-1">综合设置 <a class="header-anchor" href="#综合设置" aria-label="Permalink to &quot;综合设置&quot;">​</a></h2><h3 id="页面样式" tabindex="-1">页面样式 <a class="header-anchor" href="#页面样式" aria-label="Permalink to &quot;页面样式&quot;">​</a></h3><ul><li><p>圆角样式</p><p><img src="https://s.nmxc.ltd/sakurairo_vision/@2.6/options/page_style_iro.webp" alt="圆角样式"></p></li><li><p>直角样式</p><p><img src="https://s.nmxc.ltd/sakurairo_vision/@2.6/options/page_style_sakura.webp" alt="直角样式"></p></li></ul><h3 id="页面排版样式" tabindex="-1">页面排版样式 <a class="header-anchor" href="#页面排版样式" aria-label="Permalink to &quot;页面排版样式&quot;">​</a></h3><p>设置页面排版样式</p><ul><li>默认样式</li><li>Github 样式</li></ul><h3 id="页面装饰图片" tabindex="-1">页面装饰图片 <a class="header-anchor" href="#页面装饰图片" aria-label="Permalink to &quot;页面装饰图片&quot;">​</a></h3><p>特色图片将显示在：文章页面、独立页面和分类页</p><h3 id="页面标题动画" tabindex="-1">页面标题动画 <a class="header-anchor" href="#页面标题动画" aria-label="Permalink to &quot;页面标题动画&quot;">​</a></h3><p>开启之后页面标题将有浮入动画</p><h3 id="页面标题动画时间" tabindex="-1">页面标题动画时间 <a class="header-anchor" href="#页面标题动画时间" aria-label="Permalink to &quot;页面标题动画时间&quot;">​</a></h3><p>滑动滑块设置，推荐数值范围为 1-2</p><h3 id="在复制页面内容时添加引用信息" tabindex="-1">在复制页面内容时添加引用信息 <a class="header-anchor" href="#在复制页面内容时添加引用信息" aria-label="Permalink to &quot;在复制页面内容时添加引用信息&quot;">​</a></h3><p>开启之后用户在复制文字内容超过 30 字节时，会添加版权提示文本</p><p>像这样： <code>ref(APA): Michael Novotny.Fuukei.http://docs.fuukei.org. Retrieved 2024/11/15.</code></p><h3 id="页面-lazyload" tabindex="-1">页面 LazyLoad <a class="header-anchor" href="#页面-lazyload" aria-label="Permalink to &quot;页面 LazyLoad&quot;">​</a></h3><p>设置页面 LazyLoad 加载加载是否开启显示，开启后页面图片会有 LazyLoad 加载效果</p><p><strong>警告：WordPress 区块编辑器自带图像 LazyLoad，非必要不用开启；其他插件若启用 LazyLoad 可能会造成冲突，如 EWWW Image Optimizer.</strong></p><ul><li><p>页面 LazyLoad 加载占位 SVG：填写 SVG 链接</p></li><li><p>页面图像加载占位 SVG：填写 SVG 链接</p></li></ul><h2 id="文章页面设置" tabindex="-1">文章页面设置 <a class="header-anchor" href="#文章页面设置" aria-label="Permalink to &quot;文章页面设置&quot;">​</a></h2><h3 id="文章页面标题字体大小" tabindex="-1">文章页面标题字体大小 <a class="header-anchor" href="#文章页面标题字体大小" aria-label="Permalink to &quot;文章页面标题字体大小&quot;">​</a></h3><p>滑动滑块设置，推荐数值范围为 28-36</p><h3 id="文章页面标题下划线动画" tabindex="-1">文章页面标题下划线动画 <a class="header-anchor" href="#文章页面标题下划线动画" aria-label="Permalink to &quot;文章页面标题下划线动画&quot;">​</a></h3><p>开启且文章设置了特色图片之后，文章标题将有从左至右延伸的下划线动画</p><h3 id="文章页面显示元素" tabindex="-1">文章页面显示元素 <a class="header-anchor" href="#文章页面显示元素" aria-label="Permalink to &quot;文章页面显示元素&quot;">​</a></h3><p>可以添加要显示的文章信息，它们将在文章标题下方显示，可以通过拖拽来控制显示顺序</p><h3 id="文章页面自动显示菜单" tabindex="-1">文章页面自动显示菜单 <a class="header-anchor" href="#文章页面自动显示菜单" aria-label="Permalink to &quot;文章页面自动显示菜单&quot;">​</a></h3><p>默认开启，文章页面会自动显示 TOC 菜单，导航文章标题（仅在浏览窗口宽度足够时显示）</p><h3 id="内联代码背景色-深色模式内联代码背景色" tabindex="-1">内联代码背景色（深色模式内联代码背景色） <a class="header-anchor" href="#内联代码背景色-深色模式内联代码背景色" aria-label="Permalink to &quot;内联代码背景色（深色模式内联代码背景色）&quot;">​</a></h3><p>定义浅色 / 深色模式下段落中内联代码的背景色；代码块背景色由高亮 JS 定义（其他设置 &gt; 低使用设置）</p><h2 id="文章扩展区" tabindex="-1">文章扩展区 <a class="header-anchor" href="#文章扩展区" aria-label="Permalink to &quot;文章扩展区&quot;">​</a></h2><h3 id="文章页面功能栏" tabindex="-1">文章页面功能栏 <a class="header-anchor" href="#文章页面功能栏" aria-label="Permalink to &quot;文章页面功能栏&quot;">​</a></h3><p>关闭后图片中功能栏将在页面中隐藏</p><p><img src="'+s+'" alt="功能栏"></p><p>从左到右依次为文章知识共享协议、作者头像、作者昵称、作者个人介绍、文章最后更新时间、文章标签<br> 在文章页面设置页均有其对应的设置项决定是否开启，目前暂无法更改其显示顺序</p><h3 id="文章页面赞赏功能" tabindex="-1">文章页面赞赏功能 <a class="header-anchor" href="#文章页面赞赏功能" aria-label="Permalink to &quot;文章页面赞赏功能&quot;">​</a></h3><h4 id="赞赏图标链接" tabindex="-1">赞赏图标链接 <a class="header-anchor" href="#赞赏图标链接" aria-label="Permalink to &quot;赞赏图标链接&quot;">​</a></h4><p>填写点击赞赏图标会跳转的链接</p><h4 id="赞赏图片、链接" tabindex="-1">赞赏图片、链接 <a class="header-anchor" href="#赞赏图片、链接" aria-label="Permalink to &quot;赞赏图片、链接&quot;">​</a></h4><p>可以摆放图片以及链接，鼠标悬浮在赞赏图标上将会显示这些内容，链接就是点击对应图片会跳转的页面。</p><h3 id="文章页面作者头像-文章页面作者名字-文章页面作者签名-文章最后更新时间" tabindex="-1">文章页面作者头像&amp;文章页面作者名字&amp;文章页面作者签名&amp;文章最后更新时间&amp;&amp; <a class="header-anchor" href="#文章页面作者头像-文章页面作者名字-文章页面作者签名-文章最后更新时间" aria-label="Permalink to &quot;文章页面作者头像&amp;文章页面作者名字&amp;文章页面作者签名&amp;文章最后更新时间&amp;&amp;&quot;">​</a></h3><p>控制文章功能栏相关信息是否显示</p><h3 id="文章页面上下文章切换" tabindex="-1">文章页面上下文章切换 <a class="header-anchor" href="#文章页面上下文章切换" aria-label="Permalink to &quot;文章页面上下文章切换&quot;">​</a></h3><p>开启之后文章页面将出现上下文章切换，位于功能区下方</p><h2 id="模板页面设置" tabindex="-1">模板页面设置 <a class="header-anchor" href="#模板页面设置" aria-label="Permalink to &quot;模板页面设置&quot;">​</a></h2><h2 id="共用设置" tabindex="-1">共用设置 <a class="header-anchor" href="#共用设置" aria-label="Permalink to &quot;共用设置&quot;">​</a></h2><h3 id="模板页面标题字体大小" tabindex="-1">模板页面标题字体大小 <a class="header-anchor" href="#模板页面标题字体大小" aria-label="Permalink to &quot;模板页面标题字体大小&quot;">​</a></h3><p>设置模板页面标题字体大小，如模板中“时光轴 Timeline”等</p><h2 id="追番模板设置" tabindex="-1">追番模板设置 <a class="header-anchor" href="#追番模板设置" aria-label="Permalink to &quot;追番模板设置&quot;">​</a></h2><h3 id="追番模板源" tabindex="-1">追番模板源 <a class="header-anchor" href="#追番模板源" aria-label="Permalink to &quot;追番模板源&quot;">​</a></h3><p>支持 <a href="https://bilibili.com/" target="_blank" rel="noreferrer">Bilibili</a> 、 <a href="https://myanimelist.net/" target="_blank" rel="noreferrer">MyAnimeList</a> 以及 <a href="https://bangumi.tv/" target="_blank" rel="noreferrer">Bangumi</a></p><h3 id="my-anime-list-配置" tabindex="-1">My Anime List 配置 <a class="header-anchor" href="#my-anime-list-配置" aria-label="Permalink to &quot;My Anime List 配置&quot;">​</a></h3><p>用户名：右上角的用户名称，也可在个人资料的 URL 找到（例如 <a href="https://myanimelist.net/profile/xxx%EF%BC%89" target="_blank" rel="noreferrer">https://myanimelist.net/profile/xxx）</a></p><p>顺序：略</p><h3 id="哔哩哔哩追番模板" tabindex="-1">哔哩哔哩追番模板 <a class="header-anchor" href="#哔哩哔哩追番模板" aria-label="Permalink to &quot;哔哩哔哩追番模板&quot;">​</a></h3><ul><li>哔哩哔哩 UID：略</li><li>哔哩哔哩帐号 cookies：请求标头中的 cookies（开发者工具 &gt; “网络”选项卡）可<a href="https://blog.ukenn.top/sakura5/#toc-head-3" target="_blank" rel="noreferrer">查看教程</a>了解如何获取帐号 cookies</li></ul><p>添加模板页面后在 <code>iro 主题设置</code> 中的 <code>页面设置 &gt; 文章页面设置 &gt; 哔哩哔哩追番模板帐号 cookies</code> 中设置。</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>提示：“哔哩哔哩 cookies”在 Bilibili 被选为追番模板源时不添加 cookies 可能会报错。</p></div><h3 id="bangumi追番设置" tabindex="-1">Bangumi追番设置 <a class="header-anchor" href="#bangumi追番设置" aria-label="Permalink to &quot;Bangumi追番设置&quot;">​</a></h3><p>填写用户名ID即可，你可以从你的主页链接中获取它。</p><h3 id="使用缓存或预设响应内容" tabindex="-1">使用缓存或预设响应内容 <a class="header-anchor" href="#使用缓存或预设响应内容" aria-label="Permalink to &quot;使用缓存或预设响应内容&quot;">​</a></h3><p>当你的服务器网络不佳时可以开启，<br> 你可以填写并保存相关设置项后点击底部对应链接复制粘贴响应内容后粘贴进文本框中，<br> 或者进入你的追番页等待服务器自行获取并填充它。<br> 对bilibili分页支援不佳。<br> steam库存操作同理。</p><h2 id="友情链接模板设置" tabindex="-1">友情链接模板设置 <a class="header-anchor" href="#友情链接模板设置" aria-label="Permalink to &quot;友情链接模板设置&quot;">​</a></h2><p>友情链接模板启用之后，请在 wordpress 自带的“链接”版块中添加链接。</p><h3 id="友情链接模板单元对齐方向" tabindex="-1">友情链接模板单元对齐方向 <a class="header-anchor" href="#友情链接模板单元对齐方向" aria-label="Permalink to &quot;友情链接模板单元对齐方向&quot;">​</a></h3><div style="display:block;"><img src="https://s.nmxc.ltd/sakurairo_vision/@2.6/options/friend_link_left.webp" alt="friend_link_left" style="margin:0px 1%;display:inline-block;width:31%;"><img src="https://s.nmxc.ltd/sakurairo_vision/@2.6/options/friend_link_right.webp" alt="friend_link_right" style="margin:0px 1%;display:inline-block;width:31%;"><img src="https://s.nmxc.ltd/sakurairo_vision/@2.6/options/friend_link_center.webp" alt="friend_link_center" style="margin:0px 1%;display:inline-block;width:31%;"></div><h3 id="友情链接模板单元搭配色" tabindex="-1">友情链接模板单元搭配色 <a class="header-anchor" href="#友情链接模板单元搭配色" aria-label="Permalink to &quot;友情链接模板单元搭配色&quot;">​</a></h3><p>友情链接头像卡片中头像图片和说明文字分隔波浪线的颜色。</p><h3 id="友情链接模板单元边框阴影颜色" tabindex="-1">友情链接模板单元边框阴影颜色 <a class="header-anchor" href="#友情链接模板单元边框阴影颜色" aria-label="Permalink to &quot;友情链接模板单元边框阴影颜色&quot;">​</a></h3><h3 id="友情链接列表排序模式" tabindex="-1">友情链接列表排序模式 <a class="header-anchor" href="#友情链接列表排序模式" aria-label="Permalink to &quot;友情链接列表排序模式&quot;">​</a></h3><ul><li>Name：按名称排序。</li><li>Rating：按标注星级排序。</li><li>Updated：按链接更新时间顺序排序。</li><li>Rand：每次生成时随机排序，若存在页面缓存插件则每次重新生成缓存时随机排序；可将模板页面列入缓存排除列表以获得每次加载随机排序。</li></ul><h3 id="升序或降序" tabindex="-1">升序或降序 <a class="header-anchor" href="#升序或降序" aria-label="Permalink to &quot;升序或降序&quot;">​</a></h3><p>控制排序为升序 / 降序。</p><h3 id="steam库模版" tabindex="-1">Steam库模版 <a class="header-anchor" href="#steam库模版" aria-label="Permalink to &quot;Steam库模版&quot;">​</a></h3><p>相关使用说明、帮助请参考 <a href="/Sakurairo/Steam/">Steam库模版使用说明</a>。</p><h2 id="登录模板设置" tabindex="-1">登录模板设置 <a class="header-anchor" href="#登录模板设置" aria-label="Permalink to &quot;登录模板设置&quot;">​</a></h2><h3 id="登录模板注册功能" tabindex="-1">登录模板注册功能 <a class="header-anchor" href="#登录模板注册功能" aria-label="Permalink to &quot;登录模板注册功能&quot;">​</a></h3><p>开启之后登录模板将允许注册</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>提示：“友情链接模板”在后台左侧“链接”添加；“时光轴”模板页面建设后自动获取内容。</p></div><h2 id="评论相关设置" tabindex="-1">评论相关设置 <a class="header-anchor" href="#评论相关设置" aria-label="Permalink to &quot;评论相关设置&quot;">​</a></h2><h3 id="评论区分页与嵌套说明" tabindex="-1">评论区分页与嵌套说明 <a class="header-anchor" href="#评论区分页与嵌套说明" aria-label="Permalink to &quot;评论区分页与嵌套说明&quot;">​</a></h3><p>本主题支持评论区分页与嵌套功能，请前往 <code>WordPress 后台 -&gt; 设置 -&gt; 讨论</code> 中自行设置分页相关规则与最大支持嵌套层级</p><h3 id="页面评论区域显示" tabindex="-1">页面评论区域显示 <a class="header-anchor" href="#页面评论区域显示" aria-label="Permalink to &quot;页面评论区域显示&quot;">​</a></h3><p>展开 / 收缩</p><h3 id="自定义评论区占位内容" tabindex="-1">自定义评论区占位内容 <a class="header-anchor" href="#自定义评论区占位内容" aria-label="Permalink to &quot;自定义评论区占位内容&quot;">​</a></h3><p>就是评论框中默认显示的文字，访客没有填内容时会固定显示在里面，有文字后会移动到左上角。</p><h3 id="自定义评论提交按钮文本" tabindex="-1">自定义评论提交按钮文本 <a class="header-anchor" href="#自定义评论提交按钮文本" aria-label="Permalink to &quot;自定义评论提交按钮文本&quot;">​</a></h3><p>就是评论提交按钮上的字。</p><h3 id="页面评论区域右下背景图片" tabindex="-1">页面评论区域右下背景图片 <a class="header-anchor" href="#页面评论区域右下背景图片" aria-label="Permalink to &quot;页面评论区域右下背景图片&quot;">​</a></h3><p>评论框背景图</p><h2 id="评论区域功能" tabindex="-1">评论区域功能 <a class="header-anchor" href="#评论区域功能" aria-label="Permalink to &quot;评论区域功能&quot;">​</a></h2><h3 id="评论区表情" tabindex="-1">评论区表情 <a class="header-anchor" href="#评论区表情" aria-label="Permalink to &quot;评论区表情&quot;">​</a></h3><p>可以决定评论区允许使用哪些表情图片</p><h3 id="自定义表情包名称" tabindex="-1">自定义表情包名称 <a class="header-anchor" href="#自定义表情包名称" aria-label="Permalink to &quot;自定义表情包名称&quot;">​</a></h3><p>自定义表情包名称</p><h3 id="自定义表情包路径" tabindex="-1">自定义表情包路径 <a class="header-anchor" href="#自定义表情包路径" aria-label="Permalink to &quot;自定义表情包路径&quot;">​</a></h3><p>将自定义表情包上传到服务器后点击下方蓝色“这里”超链接可以更新自定义表情包列表</p><blockquote><p>目录如何写？<br> wordpress 6.6.2 windows+apache+php8.2环境<br><code>../.././uploads/2024/10</code> 这个例子将读取服务器的<code>wp-content\\uploads\\2024\\10</code>文件夹，<br> 并将其中的&#39;jpg&#39;, &#39;jpeg&#39;, &#39;png&#39;, &#39;gif&#39;, &#39;svg&#39;, &#39;avif&#39;, &#39;webp&#39;文件添加为表情包，<br><code>../</code>表示往上翻文件，<code>./</code>表示在这边往下翻文件，<code>/</code>表示在当前文件夹往下翻指定文件夹，<br> 请以<code>wp-content\\themes\\Sakurairo</code>为起点开始编写翻文件夹的方法。</p></blockquote><blockquote><p>wordpress6.6.2 linux+nginx+php8.2环境 程序默认起始目录是<code>/wp-contents/uploads/</code>，<br> 设置<code>/2024/10</code>即可读取<code>wp-content\\uploads\\2024\\10</code>， 具体编辑方法相同</p></blockquote><div class="important custom-block github-alert"><p class="custom-block-title">IMPORTANT</p><p>请根据自己的详细情况酌情调整目录设置。<br> 路径设置只有在点击保存后才会生效。</p></div><h3 id="自定义表情包代理地址" tabindex="-1">自定义表情包代理地址 <a class="header-anchor" href="#自定义表情包代理地址" aria-label="Permalink to &quot;自定义表情包代理地址&quot;">​</a></h3><p>填写表情图片的 CDN 地址。如果留空，则不会启用 CDN 代理功能。<br> 这个功能会替换表情包图片指向的前一部分链接，请确保相关图片在替换为相关链接后可以被正常访问。</p><p>用法与图片cdn类似。</p><h3 id="页面评论区域-ua-信息" tabindex="-1">页面评论区域 UA 信息 <a class="header-anchor" href="#页面评论区域-ua-信息" aria-label="Permalink to &quot;页面评论区域 UA 信息&quot;">​</a></h3><p>显示用户的浏览器，操作系统信息（桌面端显示图标 + 信息；手机端仅显示图标）</p><h3 id="页面评论区域位置信息" tabindex="-1">页面评论区域位置信息 <a class="header-anchor" href="#页面评论区域位置信息" aria-label="Permalink to &quot;页面评论区域位置信息&quot;">​</a></h3><p>开启后，将在评论中添加用户的IP地理位置信息。</p><p><img src="https://github.com/mirai-mamori/Sakurairo/assets/79365334/83882fae-a076-4d32-bebd-4f99c2eb1643" alt="image"></p><ul><li>对于直辖市，地理位置的显示可能有所不同，如显示为：<code> 中国 上海 上海</code></li><li>对于中国大陆区域外的地址，可能无法做到完全的汉化，有可能会显示为：<code>香港 Central and Western District Central</code>、<code> 美国 华盛顿州 Seattle</code></li><li>显示<code>Reserved Address</code>，表示获取到的用户地址是一个私有地址。如果启用了反向代理，你需要进行额外的操作才能获取到用户的真实IP地址，请查阅其他资料以获得支持。</li><li>显示<code>Empty Address</code>，表示用户提交评论时，WordPress未能获取到用户地址。IP地址记录由WordPress自动记录，这一问题并非主题造成，请查阅其他资料以获得支持。</li><li>显示<code>Unknown</code>，表示IP地理位置信息解析失败。有可能是网络异常、IP地址解析接口异常或者解析请求超出API接口速率造成的，请查阅WordPress的Debug日志获取更多信息。</li></ul><h3 id="管理页面显示位置信息" tabindex="-1">管理页面显示位置信息 <a class="header-anchor" href="#管理页面显示位置信息" aria-label="Permalink to &quot;管理页面显示位置信息&quot;">​</a></h3><p>开启后将在后台的评论管理页面中显示评论者的IP地理位置信息。</p><p><img src="https://github.com/mirai-mamori/Sakurairo/assets/79365334/e0b1538e-96a6-4192-a891-b5720f971140" alt="image"></p><h3 id="位置信息持久化" tabindex="-1">位置信息持久化 <a class="header-anchor" href="#位置信息持久化" aria-label="Permalink to &quot;位置信息持久化&quot;">​</a></h3><p>将评论者的IP地理位置信息存储到数据库中。</p><ul><li>开启后，会将用户的IP地理位置信息作为评论的元数据永久存储在数据库中，数据插入会在首次解析完成后进行。再次解析时将直接从数据库获取，而不是通过API接口进行解析，相对来说，会使得页面加载速度得到提高。</li><li>如果关闭位置信息持久化，新评论的IP地理位置将不再存储到数据库，而是实时通过API接口解析，可能会造成页面加载速度缓慢，尤其是在存在大量评论的情况下。</li><li>关闭位置信息持久化，并不会清除已存储的IP地理位置信息。在删除评论时，该条评论的IP地理位置信息也会被一起删除。</li></ul><h3 id="地理位置解析接口" tabindex="-1">地理位置解析接口 <a class="header-anchor" href="#地理位置解析接口" aria-label="Permalink to &quot;地理位置解析接口&quot;">​</a></h3><h4 id="ip-api" tabindex="-1">IP-API <a class="header-anchor" href="#ip-api" aria-label="Permalink to &quot;IP-API&quot;">​</a></h4><p>主题采用IP-API进行解析IP地理位置，<br> 由<a href="https://ip-api.com/" target="_blank" rel="noreferrer">ip-api.com</a>提供，可免费使用于非商业用途。API接口速率限制为45次/分钟，如果出现了<code>Unknown</code>，可等待片刻再次刷新页面重新请求IP地理位置解析，推荐启用<code>位置信息持久化</code>功能，以减少请求的数量，加快响应速度。主题已经配置了速率限制，不必担心超出速率限制导致IP被限制。</p><p>对于大部分区域，可获得相对准确的IP地理位置信息，并可根据WordPress的语言设置，返回相应语言的IP地理位置信息。<a href="https://ip-api.com/docs/api:json" target="_blank" rel="noreferrer">查看支持的语言</a></p><h3 id="私人评论功能" tabindex="-1">私人评论功能 <a class="header-anchor" href="#私人评论功能" aria-label="Permalink to &quot;私人评论功能&quot;">​</a></h3><p>开启之后将允许用户设置自己的评论对其他人不可见，仅博客主可见</p><h3 id="页面评论区域机器人验证" tabindex="-1">页面评论区域机器人验证 <a class="header-anchor" href="#页面评论区域机器人验证" aria-label="Permalink to &quot;页面评论区域机器人验证&quot;">​</a></h3><p>开启后评论需要勾选“我不是机器人”</p><h3 id="qq-头像链接加密" tabindex="-1">QQ 头像链接加密 <a class="header-anchor" href="#qq-头像链接加密" aria-label="Permalink to &quot;QQ 头像链接加密&quot;">​</a></h3><ul><li>重定向</li><li>后端获取头像数据</li><li>后端解析头像接口</li></ul><h3 id="页面评论区域上传图片接口" tabindex="-1">页面评论区域上传图片接口 <a class="header-anchor" href="#页面评论区域上传图片接口" aria-label="Permalink to &quot;页面评论区域上传图片接口&quot;">​</a></h3><p>支持图床列表：</p><ul><li>Imgur</li><li>SM. MS</li><li>Chevereto（可自建）</li><li>Lsky Pro（可自建）</li></ul><h3 id="imgur-client-id" tabindex="-1">Imgur Client ID <a class="header-anchor" href="#imgur-client-id" aria-label="Permalink to &quot;Imgur Client ID&quot;">​</a></h3><h3 id="sm-ms-token" tabindex="-1">SM. MS Token <a class="header-anchor" href="#sm-ms-token" aria-label="Permalink to &quot;SM. MS Token&quot;">​</a></h3><h3 id="chevereto-key" tabindex="-1">Chevereto Key <a class="header-anchor" href="#chevereto-key" aria-label="Permalink to &quot;Chevereto Key&quot;">​</a></h3><h3 id="lsky-pro-v1-token" tabindex="-1">Lsky Pro v1 Token <a class="header-anchor" href="#lsky-pro-v1-token" aria-label="Permalink to &quot;Lsky Pro v1 Token&quot;">​</a></h3><p>以上详见图床平台支持信息。</p><h3 id="评论图片代理" tabindex="-1">评论图片代理 <a class="header-anchor" href="#评论图片代理" aria-label="Permalink to &quot;评论图片代理&quot;">​</a></h3><p>已弃用。</p><h2 id="评论邮件通知设置" tabindex="-1">评论邮件通知设置 <a class="header-anchor" href="#评论邮件通知设置" aria-label="Permalink to &quot;评论邮件通知设置&quot;">​</a></h2><h3 id="用户邮件回复通知" tabindex="-1">用户邮件回复通知 <a class="header-anchor" href="#用户邮件回复通知" aria-label="Permalink to &quot;用户邮件回复通知&quot;">​</a></h3><p>WordPress 默认会使用邮件通知用户评论收到回复，开启之后允许用户设置自己的评论收到回复时是否使用邮件通知</p><h3 id="管理员邮件回复通知" tabindex="-1">管理员邮件回复通知 <a class="header-anchor" href="#管理员邮件回复通知" aria-label="Permalink to &quot;管理员邮件回复通知&quot;">​</a></h3><p>开启之后当管理员评论收到回复时使用邮件通知</p>',140))])}const g=t(c,[["render",u]]);export{x as __pageData,g as default};
