import{_ as i,a as n}from"./chunks/form.BswjN2dI.js";import{_ as r,c as l,j as a,a as t,G as s,a2 as d,B as h,o as m}from"./chunks/framework.DPuwY6B9.js";const y=JSON.parse('{"title":"Page Options","description":"","frontmatter":{"title":"Page Options"},"headers":[],"relativePath":"en/Sakurairo/Pages/index.md","filePath":"en/Sakurairo/Pages/index.md"}'),c={name:"en/Sakurairo/Pages/index.md"},p={id:"page-options",tabindex:"-1"};function u(g,e,b,f,k,P){const o=h("Badge");return m(),l("div",null,[a("h1",p,[e[0]||(e[0]=t("Page Options ")),s(o,{type:"tip",text:"v3.0"}),e[1]||(e[1]=t()),e[2]||(e[2]=a("a",{class:"header-anchor",href:"#page-options","aria-label":'Permalink to "Page Options <Badge type="tip" text="v3.0" />"'},"​",-1))]),e[3]||(e[3]=d('<h2 id="general-options" tabindex="-1">General Options <a class="header-anchor" href="#general-options" aria-label="Permalink to &quot;General Options&quot;">​</a></h2><h3 id="page-layout-style" tabindex="-1">Page Layout Style <a class="header-anchor" href="#page-layout-style" aria-label="Permalink to &quot;Page Layout Style&quot;">​</a></h3><p>Choose the page typography style:</p><ul><li>Default style</li><li>GitHub style</li></ul><h3 id="decorative-image" tabindex="-1">Decorative Image <a class="header-anchor" href="#decorative-image" aria-label="Permalink to &quot;Decorative Image&quot;">​</a></h3><p>The featured image will display on post pages, standalone pages, and category pages.</p><h3 id="page-title-animation" tabindex="-1">Page Title Animation <a class="header-anchor" href="#page-title-animation" aria-label="Permalink to &quot;Page Title Animation&quot;">​</a></h3><p>Enable a fade-in animation for the page title.</p><h3 id="title-animation-duration" tabindex="-1">Title Animation Duration <a class="header-anchor" href="#title-animation-duration" aria-label="Permalink to &quot;Title Animation Duration&quot;">​</a></h3><p>Drag the slider to set the animation length (recommended: 1–2 seconds).</p><h3 id="add-attribution-when-copying" tabindex="-1">Add Attribution When Copying <a class="header-anchor" href="#add-attribution-when-copying" aria-label="Permalink to &quot;Add Attribution When Copying&quot;">​</a></h3><p>When enabled, copying over 30 bytes of text appends a citation, e.g.:</p><p><code>ref(APA): Michael Novotny.Fuukei.http://docs.fuukei.org. Retrieved 2024/11/15.</code></p><h3 id="page-lazyload" tabindex="-1">Page LazyLoad <a class="header-anchor" href="#page-lazyload" aria-label="Permalink to &quot;Page LazyLoad&quot;">​</a></h3><p>Enable LazyLoad for images on pages.<br><strong>Warning: The block editor already uses LazyLoad; enabling here may conflict with plugins like EWWW Image Optimizer.</strong></p><ul><li><p>Placeholder SVG for page LazyLoad: enter the SVG URL.</p></li><li><p>Placeholder SVG for image LazyLoad: enter the SVG URL.</p></li></ul><h2 id="post-page-options" tabindex="-1">Post Page Options <a class="header-anchor" href="#post-page-options" aria-label="Permalink to &quot;Post Page Options&quot;">​</a></h2><h3 id="post-title-font-size" tabindex="-1">Post Title Font Size <a class="header-anchor" href="#post-title-font-size" aria-label="Permalink to &quot;Post Title Font Size&quot;">​</a></h3><p>Drag the slider to adjust title size (recommended: 28–36 px).</p><h3 id="underline-animation-for-title" tabindex="-1">Underline Animation for Title <a class="header-anchor" href="#underline-animation-for-title" aria-label="Permalink to &quot;Underline Animation for Title&quot;">​</a></h3><p>When enabled and a featured image is set, adds a left-to-right underline effect under the title.</p><h3 id="display-elements" tabindex="-1">Display Elements <a class="header-anchor" href="#display-elements" aria-label="Permalink to &quot;Display Elements&quot;">​</a></h3><p>Select which metadata to show under the post title. Drag to reorder.</p><h3 id="auto-show-toc" tabindex="-1">Auto-Show TOC <a class="header-anchor" href="#auto-show-toc" aria-label="Permalink to &quot;Auto-Show TOC&quot;">​</a></h3><p>Enabled by default: displays a table of contents on wide viewports.</p><h3 id="inline-code-background-color" tabindex="-1">Inline Code Background Color <a class="header-anchor" href="#inline-code-background-color" aria-label="Permalink to &quot;Inline Code Background Color&quot;">​</a></h3><p>Set the background color for inline code in light/dark mode. (Code block backgrounds come from your syntax highlighter options.)</p><h2 id="post-extension-area" tabindex="-1">Post Extension Area <a class="header-anchor" href="#post-extension-area" aria-label="Permalink to &quot;Post Extension Area&quot;">​</a></h2><h3 id="post-utility-bar" tabindex="-1">Post Utility Bar <a class="header-anchor" href="#post-utility-bar" aria-label="Permalink to &quot;Post Utility Bar&quot;">​</a></h3><p>Disable to hide the bar of icons over images.<br><img src="'+i+'" alt=""><br> Icons left-to-right: CC license, author avatar, name, bio, last update, tags. Each has its own toggle in Post Page Options.</p><h3 id="reward-feature" tabindex="-1">“Reward” Feature <a class="header-anchor" href="#reward-feature" aria-label="Permalink to &quot;“Reward” Feature&quot;">​</a></h3><ul><li><strong>Icon Link</strong>: URL users go to when clicking the reward icon.</li><li><strong>Pop-up Content</strong>: images and links shown on hover; clicking an image follows its link.</li></ul><h3 id="show-author-meta" tabindex="-1">Show Author &amp; Meta <a class="header-anchor" href="#show-author-meta" aria-label="Permalink to &quot;Show Author &amp; Meta&quot;">​</a></h3><p>Toggle display of avatar, name, signature, last update, etc.</p><h3 id="prev-next-post-navigation" tabindex="-1">Prev/Next Post Navigation <a class="header-anchor" href="#prev-next-post-navigation" aria-label="Permalink to &quot;Prev/Next Post Navigation&quot;">​</a></h3><p>When enabled, adds next/previous post links below the utility bar.</p><h2 id="template-page-options" tabindex="-1">Template Page Options <a class="header-anchor" href="#template-page-options" aria-label="Permalink to &quot;Template Page Options&quot;">​</a></h2><h3 id="common" tabindex="-1">Common <a class="header-anchor" href="#common" aria-label="Permalink to &quot;Common&quot;">​</a></h3><h4 id="template-title-font-size" tabindex="-1">Template Title Font Size <a class="header-anchor" href="#template-title-font-size" aria-label="Permalink to &quot;Template Title Font Size&quot;">​</a></h4><p>Set font size for titles on template pages (e.g. Timeline).</p><h2 id="anime-tracking-template" tabindex="-1">“Anime Tracking” Template <a class="header-anchor" href="#anime-tracking-template" aria-label="Permalink to &quot;“Anime Tracking” Template&quot;">​</a></h2><h3 id="source" tabindex="-1">Source <a class="header-anchor" href="#source" aria-label="Permalink to &quot;Source&quot;">​</a></h3><p>Supported: <a href="https://bilibili.com/" target="_blank" rel="noreferrer">Bilibili</a>, <a href="https://myanimelist.net/" target="_blank" rel="noreferrer">MyAnimeList</a>, <a href="https://bangumi.tv/" target="_blank" rel="noreferrer">Bangumi</a>.</p><h3 id="myanimelist" tabindex="-1">MyAnimeList <a class="header-anchor" href="#myanimelist" aria-label="Permalink to &quot;MyAnimeList&quot;">​</a></h3><ul><li><strong>Username</strong>: the name in your profile URL (<code>…/profile/xxx</code>).</li></ul><h3 id="bilibili" tabindex="-1">Bilibili <a class="header-anchor" href="#bilibili" aria-label="Permalink to &quot;Bilibili&quot;">​</a></h3><ul><li><strong>UID</strong>: your Bilibili user ID.</li><li><strong>Cookies</strong>: copy from DevTools “Network” request headers. <a href="https://blog.ukenn.top/sakura5/#toc-head-3" target="_blank" rel="noreferrer">Guide</a><br> Enter the cookie string under <code>Page Options → Post Page Options → Bilibili Template Cookies</code>.</li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Leaving out cookies for Bilibili may cause errors.</p></div><h3 id="bangumi" tabindex="-1">Bangumi <a class="header-anchor" href="#bangumi" aria-label="Permalink to &quot;Bangumi&quot;">​</a></h3><p>Enter your Bangumi user ID (found in your profile URL).</p><h3 id="cached-or-manual-responses" tabindex="-1">Cached or Manual Responses <a class="header-anchor" href="#cached-or-manual-responses" aria-label="Permalink to &quot;Cached or Manual Responses&quot;">​</a></h3><p>If your server is slow, enable this and paste saved API responses into the box, or let the page fetch and store them. Note: Bilibili pagination is limited; Steam library works similarly.</p><h2 id="friends-link-template" tabindex="-1">Friends Link Template <a class="header-anchor" href="#friends-link-template" aria-label="Permalink to &quot;Friends Link Template&quot;">​</a></h2><p>After enabling, add links in <strong>Dashboard → Links</strong>.</p><h3 id="item-alignment" tabindex="-1">Item Alignment <a class="header-anchor" href="#item-alignment" aria-label="Permalink to &quot;Item Alignment&quot;">​</a></h3><div style="display:block;"><img src="https://s.nmxc.ltd/sakurairo_vision/@3.0/options/friend_link_left.webp" alt="left" style="display:inline-block;width:31%;"><img src="https://s.nmxc.ltd/sakurairo_vision/@3.0/options/friend_link_right.webp" alt="right" style="display:inline-block;width:31%;"><img src="https://s.nmxc.ltd/sakurairo_vision/@3.0/options/friend_link_center.webp" alt="center" style="display:inline-block;width:31%;"></div><h3 id="show-application-form" tabindex="-1">Show Application Form <a class="header-anchor" href="#show-application-form" aria-label="Permalink to &quot;Show Application Form&quot;">​</a></h3><p>Enable a “Apply for Link” button next to the page title.<br><img src="'+n+'" alt=""></p><h3 id="sorting-mode" tabindex="-1">Sorting Mode <a class="header-anchor" href="#sorting-mode" aria-label="Permalink to &quot;Sorting Mode&quot;">​</a></h3><ul><li>Name: alphabetical</li><li>Rating: by star rating</li><li>Updated: by last update time</li><li>Rand: random each cache refresh (exclude template page from caching for true randomness)</li></ul><h3 id="ascending-descending" tabindex="-1">Ascending/Descending <a class="header-anchor" href="#ascending-descending" aria-label="Permalink to &quot;Ascending/Descending&quot;">​</a></h3><p>Choose sort order.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Add friend links under <strong>Links</strong> in the dashboard.</p></div><h3 id="steam-library-template" tabindex="-1">Steam Library Template <a class="header-anchor" href="#steam-library-template" aria-label="Permalink to &quot;Steam Library Template&quot;">​</a></h3><p>See <a href="/en/Sakurairo/Steam/">Steam Library Template Guide</a>.</p><h2 id="comment-options" tabindex="-1">Comment Options <a class="header-anchor" href="#comment-options" aria-label="Permalink to &quot;Comment Options&quot;">​</a></h2><h3 id="pagination-nesting" tabindex="-1">Pagination &amp; Nesting <a class="header-anchor" href="#pagination-nesting" aria-label="Permalink to &quot;Pagination &amp; Nesting&quot;">​</a></h3><p>Configure comment pagination and nesting under <strong>Dashboard → Options → Discussion</strong>.</p><h3 id="comment-area-toggle" tabindex="-1">Comment Area Toggle <a class="header-anchor" href="#comment-area-toggle" aria-label="Permalink to &quot;Comment Area Toggle&quot;">​</a></h3><p>Choose default state: expanded or collapsed.</p><h3 id="placeholder-text" tabindex="-1">Placeholder Text <a class="header-anchor" href="#placeholder-text" aria-label="Permalink to &quot;Placeholder Text&quot;">​</a></h3><p>Custom text shown inside the comment field until typed.</p><h3 id="submit-button-text" tabindex="-1">Submit Button Text <a class="header-anchor" href="#submit-button-text" aria-label="Permalink to &quot;Submit Button Text&quot;">​</a></h3><p>Customize the comment button label.</p><h3 id="comment-background-image" tabindex="-1">Comment Background Image <a class="header-anchor" href="#comment-background-image" aria-label="Permalink to &quot;Comment Background Image&quot;">​</a></h3><p>URL for the background image behind the comment area.</p><h2 id="comment-features" tabindex="-1">Comment Features <a class="header-anchor" href="#comment-features" aria-label="Permalink to &quot;Comment Features&quot;">​</a></h2><h3 id="emoticons" tabindex="-1">Emoticons <a class="header-anchor" href="#emoticons" aria-label="Permalink to &quot;Emoticons&quot;">​</a></h3><p>Enable specific emoji sets in comments.</p><h3 id="custom-emoticon-pack" tabindex="-1">Custom Emoticon Pack <a class="header-anchor" href="#custom-emoticon-pack" aria-label="Permalink to &quot;Custom Emoticon Pack&quot;">​</a></h3><ul><li><strong>Name</strong>: your pack name.</li><li><strong>Path</strong>: upload your files, then click “here” to refresh the list. <ul><li><strong>Windows example</strong>: <code>../.././uploads/2024/10</code> loads from <code>wp-content\\uploads\\2024\\10</code>.</li><li><strong>Linux example</strong>: <code>/2024/10</code> loads from <code>/wp-content/uploads/2024/10</code>.<br> Adjust paths as needed; save to apply.</li></ul></li></ul><h3 id="emoticon-cdn-proxy" tabindex="-1">Emoticon CDN Proxy <a class="header-anchor" href="#emoticon-cdn-proxy" aria-label="Permalink to &quot;Emoticon CDN Proxy&quot;">​</a></h3><p>Enter a CDN base URL to rewrite emoticon URLs. Leave empty to disable.</p><h3 id="show-commenter-ua" tabindex="-1">Show Commenter UA <a class="header-anchor" href="#show-commenter-ua" aria-label="Permalink to &quot;Show Commenter UA&quot;">​</a></h3><p>Display browser and OS (desktop shows icon + text; mobile icon only).</p><h3 id="commenter-geolocation" tabindex="-1">Commenter Geolocation <a class="header-anchor" href="#commenter-geolocation" aria-label="Permalink to &quot;Commenter Geolocation&quot;">​</a></h3><p>Enable IP-to-location in comments.</p><ul><li>Municipalities show “China Shanghai Shanghai.”</li><li>Outside mainland may include English.</li><li>“Reserved Address” = private IP (requires proxy).</li><li>“Empty Address” = no IP captured (WP limitation).</li><li>“Unknown” = lookup failed or rate-limited.</li></ul><h3 id="admin-geolocation" tabindex="-1">Admin Geolocation <a class="header-anchor" href="#admin-geolocation" aria-label="Permalink to &quot;Admin Geolocation&quot;">​</a></h3><p>Show commenter location in dashboard comment list.<br><img src="https://github.com/mirai-mamori/Sakurairo/assets/79365334/e0b1538e-96a6-4192-a891-b5720f971140" alt="image"></p><h3 id="persist-location-data" tabindex="-1">Persist Location Data <a class="header-anchor" href="#persist-location-data" aria-label="Permalink to &quot;Persist Location Data&quot;">​</a></h3><p>Store geolocation in comment metadata for faster loads.</p><ul><li>On: save once and reuse.</li><li>Off: each view calls API.<br> Deleting comments also removes stored data.</li></ul><h3 id="geolocation-api" tabindex="-1">Geolocation API <a class="header-anchor" href="#geolocation-api" aria-label="Permalink to &quot;Geolocation API&quot;">​</a></h3><h4 id="ip-api" tabindex="-1">IP-API <a class="header-anchor" href="#ip-api" aria-label="Permalink to &quot;IP-API&quot;">​</a></h4><p>Uses <a href="https://ip-api.com/" target="_blank" rel="noreferrer">ip-api.com</a> (free for non-commercial, 45 rpm limit).<br> Enable “Persist Location” to reduce requests. Supports multiple languages based on WP locale. <a href="https://ip-api.com/docs/api:json" target="_blank" rel="noreferrer">Supported languages</a>.</p><h3 id="private-comments" tabindex="-1">Private Comments <a class="header-anchor" href="#private-comments" aria-label="Permalink to &quot;Private Comments&quot;">​</a></h3><p>Allow commenters to mark their comment visible only to the site owner.</p><h3 id="comment-captcha" tabindex="-1">Comment CAPTCHA <a class="header-anchor" href="#comment-captcha" aria-label="Permalink to &quot;Comment CAPTCHA&quot;">​</a></h3><p>Require “I am not a robot” checkbox for comments.</p><h3 id="qq-avatar-encryption" tabindex="-1">QQ Avatar Encryption <a class="header-anchor" href="#qq-avatar-encryption" aria-label="Permalink to &quot;QQ Avatar Encryption&quot;">​</a></h3><p>Options for redirect, server-side fetch, or parsing.</p><h3 id="image-upload-in-comments" tabindex="-1">Image Upload in Comments <a class="header-anchor" href="#image-upload-in-comments" aria-label="Permalink to &quot;Image Upload in Comments&quot;">​</a></h3><p>Supported: Imgur, SM.MS, Chevereto (self-hosted), Lsky Pro (self-hosted).</p><ul><li>Enter <strong>Imgur Client ID</strong>, <strong>SM.MS Token</strong>, <strong>Chevereto Key</strong>, <strong>Lsky Pro Token</strong> as needed.</li></ul><h3 id="comment-image-proxy" tabindex="-1">Comment Image Proxy <a class="header-anchor" href="#comment-image-proxy" aria-label="Permalink to &quot;Comment Image Proxy&quot;">​</a></h3><p>Deprecated.</p><h2 id="comment-email-notifications" tabindex="-1">Comment Email Notifications <a class="header-anchor" href="#comment-email-notifications" aria-label="Permalink to &quot;Comment Email Notifications&quot;">​</a></h2><h3 id="user-reply-notification" tabindex="-1">User Reply Notification <a class="header-anchor" href="#user-reply-notification" aria-label="Permalink to &quot;User Reply Notification&quot;">​</a></h3><p>Allow users to opt in/out of email alerts for replies to their comments.</p><h3 id="admin-reply-notification" tabindex="-1">Admin Reply Notification <a class="header-anchor" href="#admin-reply-notification" aria-label="Permalink to &quot;Admin Reply Notification&quot;">​</a></h3><p>Email admins when their comments get replies.</p>',112))])}const w=r(c,[["render",u]]);export{y as __pageData,w as default};
