import{_ as r,c as l,j as i,a,G as o,a2 as n,B as s,o as d}from"./chunks/framework.DPuwY6B9.js";const k=JSON.parse('{"title":"Changelog","description":"","frontmatter":{"title":"Changelog"},"headers":[],"relativePath":"en/Sakurairo/Update-Log/index.md","filePath":"en/Sakurairo/Update-Log/index.md"}'),h={name:"en/Sakurairo/Update-Log/index.md"},c={id:"changelog",tabindex:"-1"};function u(m,e,g,p,f,b){const t=s("Badge");return d(),l("div",null,[i("h1",c,[e[0]||(e[0]=a("Changelog ")),o(t,{type:"tip",text:"By Kanochan, AstroNomen, nicocat"}),e[1]||(e[1]=a()),e[2]||(e[2]=i("a",{class:"header-anchor",href:"#changelog","aria-label":'Permalink to "Changelog <Badge type="tip" text="By Kanochan, AstroNomen, nicocat"/>"'},"​",-1))]),e[3]||(e[3]=n('<h2 id="_3-0-2" tabindex="-1">3.0.2 <a class="header-anchor" href="#_3-0-2" aria-label="Permalink to &quot;3.0.2&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/3.0.2" target="_blank" rel="noreferrer">April 24, 2025</a></p><p><img src="https://s.nmxc.ltd/sakurairo_vision/@3.0/readme/banner-cn.webp" alt="img"></p><h3 id="bug-fixes" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Corrected an issue where the latest post’s cover image was not propagated in the post list.</li><li>Resolved a console error when the “Dynamic Island” style was active without the random-cover toggle (rendering unaffected).</li><li>Fixed default code-block styling not adapting to dark mode.</li><li>Patched display anomalies for <code>&lt;ul&gt;</code> and <code>&lt;ol&gt;</code> elements.</li><li>Addressed mobile search bar misbehavior when not following configuration.</li><li>Fixed misaligned pagination controls.</li><li>Prevented errors when the homepage is set to hide all theme components.</li><li>Theoretically resolved same-origin restrictions on template pages.</li></ul><h3 id="enhancements" tabindex="-1">Enhancements <a class="header-anchor" href="#enhancements" aria-label="Permalink to &quot;Enhancements&quot;">​</a></h3><ul><li>Refactored the archive-page template for improved maintainability.</li><li>Updated GitHub repository cards to correctly adapt to dark mode.</li></ul><hr><h2 id="_3-0-1" tabindex="-1">3.0.1 <a class="header-anchor" href="#_3-0-1" aria-label="Permalink to &quot;3.0.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/3.0.1" target="_blank" rel="noreferrer">April 16, 2025</a></p><p><img src="https://s.nmxc.ltd/sakurairo_vision/@3.0/readme/banner-cn.webp" alt="img"></p><h3 id="new-features" tabindex="-1">New Features <a class="header-anchor" href="#new-features" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Introduced a new navigation style: <strong>“Dynamic Island.”</strong></li><li>Integrated the WordPress Customizer for live theme editing.</li><li>Added friend-link status management.</li><li>Implemented a self-service friend-link application workflow.</li><li>Introduced AI-powered article annotations.</li><li>Added a Steam library template and Steam status cards.</li><li>Enhanced search capabilities: now supports filtering by posts, pages, and “shuoshuo,” excluding content, and pinning sticky posts.</li><li>“Anime Tracking” templates now support <strong>Bangumi</strong>.</li><li>Launched a new built-in random-image API with WebP optimization, aspect-ratio filtering, and subdirectory management.</li><li>Added optional server-proxy support for GitHub repo cards.</li><li>Homepage now supports embedding static pages.</li><li>Added a Bilibili embed shortcode: <code>[vbilibili]BV_ID[/vbilibili]</code>.</li></ul><h3 id="bug-fixes-1" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-1" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Resolved search functionality failures.</li><li>Fixed white-screen issue on first theme activation.</li><li>Made Table of Contents scrollable when post titles exceed viewport height.</li><li>Adjusted dark-mode cover-image contrast to follow the global “Dark Mode Image Brightness” setting.</li><li>Fixed toggling of “shuoshuo” on the homepage.</li><li>Resolved child-theme creation errors.</li><li>Improved compatibility with newer WordPress versions.</li><li>Fixed timeline merging anomalies when combining entries from the same month across different years.</li><li>Patched an issue allowing password-protected post images to display before password entry when navigating prev/next.</li><li>Corrected particle-effect layering so particles are visible in dark mode.</li></ul><h3 id="optimizations" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Significantly reduced overall bundle size; removed unused functions and styles.</li><li>Rebuilt the mobile navigation bar for performance and UX.</li><li>Refactored the comments section for visual and functional improvements.</li><li>Overhauled the footer and 404 page designs.</li><li>Restyled the admin interface, login, and registration pages.</li><li>Added CSS and asset minification.</li><li>Enhanced theme-color extraction logic.</li><li>Improved social-icon configuration and infinite looping display.</li><li>Logged custom-emoticon additions to facilitate debugging.</li><li>Tweaked PHP-level log suppression to hide noncritical messages.</li><li>Refined ticket-style UI elements.</li><li>Improved in-page anchor behavior.</li><li>Updated numerous mouse-cursor icons for better intuitiveness.</li><li>Pager now honors the WordPress “Posts per page” setting.</li><li>Enhanced blockquote styling.</li><li>Added page numbers when using “Prev/Next” pagination.</li><li>Enforced <code>lv6</code> rank display for admins in comments.</li><li>Enhanced the second showcase layout.</li><li>Simplified toggling of lightbox effects.</li><li>Allowed dynamic adjustment of showcase item count.</li><li>Improved GitHub shortcode parsing: supports both <code>user/repo</code> and full URLs.</li><li>Auto-switched site language based on user preference.</li><li>Upgraded built-in CAPTCHA.</li><li>Optimized secondary-menu behavior and several setting descriptions.</li><li>Added an installation-directory check to streamline setup.</li><li>Hardened some permission checks.</li><li>Enhanced category-listing display.</li><li>Applied custom-resource paths to image-selection settings.</li><li>Removed undocumented <code>download</code> and <code>ip</code> shortcodes.</li><li>Refined filter effects.</li></ul><hr><h2 id="_2-7-2" tabindex="-1">2.7.2 <a class="header-anchor" href="#_2-7-2" aria-label="Permalink to &quot;2.7.2&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.7.2" target="_blank" rel="noreferrer">November 3, 2024</a></p><p><img src="https://p.sda1.dev/19/8fb19c945f83abb12cdeecceeb5413ce/image.png" alt="img"></p><h3 id="new-features-1" tabindex="-1">New Features <a class="header-anchor" href="#new-features-1" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added support for custom playlists via MetingAPI or static JSON playlist files.</li></ul><h3 id="bug-fixes-2" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-2" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Fixed the site-wide search engine.</li></ul><h3 id="optimizations-1" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-1" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Improved compatibility with Alpine Linux.</li></ul><hr><h2 id="_2-7-1-1" tabindex="-1">2.7.1.1 <a class="header-anchor" href="#_2-7-1-1" aria-label="Permalink to &quot;2.7.1.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.7.1.1" target="_blank" rel="noreferrer">October 20, 2024</a></p><p><img src="https://p.sda1.dev/19/8fb19c945f83abb12cdeecceeb5413ce/image.png" alt="img"></p><h3 id="bug-fixes-3" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-3" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Corrected duplicate post cards on the homepage.</li></ul><hr><h2 id="_2-7-1" tabindex="-1">2.7.1 <a class="header-anchor" href="#_2-7-1" aria-label="Permalink to &quot;2.7.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.7.1" target="_blank" rel="noreferrer">October 20, 2024</a></p><p><img src="https://p.sda1.dev/19/8fb19c945f83abb12cdeecceeb5413ce/image.png" alt="img"></p><h3 id="new-features-2" tabindex="-1">New Features <a class="header-anchor" href="#new-features-2" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>“Shuoshuo” entries can now include mood tags via the block editor.</li><li>Per-post sharing license overrides in the block editor.</li><li>Per-post title CSS customizations via the block editor.</li><li>Refactored copy-citation functionality.</li></ul><h3 id="bug-fixes-4" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-4" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Standardized license prompts vs. clipboard reminders [#934].</li><li>Ensured sticky posts remain pinned [#936].</li><li>Restored the cover wave effect [#942].</li><li>Fixed homepage card caption clipping [#942].</li><li>Patched Bilibili tracking-template requests.</li></ul><h3 id="optimizations-2" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-2" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Disabled lazy loading for avatars.</li><li>Removed white borders from post backgrounds.</li><li>Prevented loading of the first list item by default.</li></ul><hr><h2 id="_2-7-0" tabindex="-1">2.7.0 <a class="header-anchor" href="#_2-7-0" aria-label="Permalink to &quot;2.7.0&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.7.0" target="_blank" rel="noreferrer">October 13, 2024</a></p><p><img src="https://p.sda1.dev/19/8fb19c945f83abb12cdeecceeb5413ce/image.png" alt="img"></p><h3 id="new-features-3" tabindex="-1">New Features <a class="header-anchor" href="#new-features-3" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Redesigned homepage post-listing layout.</li><li>Updated social-icon set.</li><li>Introduced “shuoshuo” and mood-tag functionality.</li><li>Visual refresh of author profile pages.</li><li>Added AI self-test and model-swap capabilities.</li><li>Refactored article licensing to support custom CC protocols.</li><li>Exposed custom post-meta fields.</li></ul><h3 id="bug-fixes-5" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-5" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Addressed numerous bugs (and introduced a few new ones).</li></ul><h3 id="optimizations-3" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-3" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Enhanced overall theme performance and stability.</li></ul><hr><h2 id="_2-6-3-1" tabindex="-1">2.6.3.1 <a class="header-anchor" href="#_2-6-3-1" aria-label="Permalink to &quot;2.6.3.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.6.3.1" target="_blank" rel="noreferrer">November 1, 2023</a></p><p><img src="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" alt="img"></p><h3 id="bug-fixes-6" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-6" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Fixed unexpected errors on initial activation [#801].</li><li>Corrected overlapping text in the “shuoshuo” template [#799].</li><li>Patched Typed.js failure when loading from public CDN [#803].</li></ul><h3 id="new-features-4" tabindex="-1">New Features <a class="header-anchor" href="#new-features-4" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>None – maintenance release.</li></ul><h3 id="optimizations-4" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-4" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Improved image-SEO metadata.</li></ul><hr><h2 id="_2-6-3" tabindex="-1">2.6.3 <a class="header-anchor" href="#_2-6-3" aria-label="Permalink to &quot;2.6.3&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.6.3" target="_blank" rel="noreferrer">October 11, 2023</a></p><p><img src="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" alt="img"></p><h3 id="bug-fixes-7" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-7" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Corrected style errors in prev/next post navigation.</li><li>Fixed “shuoshuo” template styling.</li><li>Restored QQ user-info retrieval in comments.</li><li>Suppressed PHP deprecated warnings [#760].</li><li>Enhanced compatibility with security plugins.</li><li>Resolved intermittent login-form input failure on reload [#773].</li><li>Fixed invalid CAPTCHA on password-reset page [#777].</li></ul><h3 id="new-features-5" tabindex="-1">New Features <a class="header-anchor" href="#new-features-5" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added custom-emoticon packs to comments (<a href="/en/Sakurairo/Pages/#custom-emoticon-pack">usage guide</a>).</li></ul><h3 id="optimizations-5" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-5" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Streamlined settings panel.</li><li>Refined comment-section styling.</li><li>Added timestamps to uploaded images.</li><li>Avoided Vaptcha if keys are unset.</li></ul><hr><h2 id="_2-6-2" tabindex="-1">2.6.2 <a class="header-anchor" href="#_2-6-2" aria-label="Permalink to &quot;2.6.2&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/releases/tag/2.6.2" target="_blank" rel="noreferrer">June 13, 2023</a></p><p><a href="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" target="_blank" rel="noreferrer"><img src="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" alt="img"></a></p><h3 id="bug-fixes-8" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-8" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Fixed nested list styling errors in posts [#729].</li><li>Patched Bilibili favorites-template errors [#699], [#700].</li><li>Corrected 404-page asset paths.</li><li>Ensured empty search results return 404 status (<a href="https://ivampiresp.com/2023/05/31/fix-the-issue-of-wordpress-returning-200-when-no-content" target="_blank" rel="noreferrer">details</a>).</li><li>Patched Typed.js loading failures from public CDN [#741].</li></ul><h3 id="new-features-6" tabindex="-1">New Features <a class="header-anchor" href="#new-features-6" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added pagination for “shuoshuo” pages.</li></ul><h3 id="optimizations-6" tabindex="-1">Optimizations <a class="header-anchor" href="#optimizations-6" aria-label="Permalink to &quot;Optimizations&quot;">​</a></h3><ul><li>Unified “shuoshuo” styling with post content.</li><li>Removed redundant settings; refined grouping.</li><li>Switched Google Fonts API to <a href="https://tianli-blog.club/" target="_blank" rel="noreferrer">Tianli</a>.</li><li>Changed default Gravatar proxy to <a href="https://weavatar.com/" target="_blank" rel="noreferrer">WeAvatar</a>.</li></ul><hr><h2 id="_2-6-1-1" tabindex="-1">2.6.1.1 <a class="header-anchor" href="#_2-6-1-1" aria-label="Permalink to &quot;2.6.1.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/tree/2.6.1.1" target="_blank" rel="noreferrer">May 15, 2023</a></p><p><a href="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" target="_blank" rel="noreferrer"><img src="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" alt="img"></a></p><h3 id="bug-fixes-9" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-9" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Resolved inline‐code and code‐block background color conflict [#722].</li><li>Fixed malformed <code>&lt;a&gt;</code> tags in “shuoshuo” content.</li><li>Excluded hidden categories when determining prev/next posts.</li><li>Patched <code>check_title_tags()</code> errors on empty content (affecting friend-links).</li><li>Added usage hints for certain widgets.</li><li>Prevented navbar overflow in edge cases.</li></ul><h3 id="new-features-7" tabindex="-1">New Features <a class="header-anchor" href="#new-features-7" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Enabled custom sorting modes for friend-link lists (name, rating, update time, random).</li></ul><hr><h2 id="_2-6-1" tabindex="-1">2.6.1 <a class="header-anchor" href="#_2-6-1" aria-label="Permalink to &quot;2.6.1&quot;">​</a></h2><p><strong>Release Date</strong>: <a href="https://github.com/mirai-mamori/Sakurairo/tree/2.6.1" target="_blank" rel="noreferrer">May 12, 2023</a></p><p><a href="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" target="_blank" rel="noreferrer"><img src="https://image.kanochan.net/2023/05/15/2023051515095264f5fa4589cc8d7bf.png" alt="img"></a></p><h3 id="bug-fixes-10" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes-10" aria-label="Permalink to &quot;Bug Fixes&quot;">​</a></h3><ul><li>Fixed persistent TOC on friend-links when no headings exist.</li><li>Prevented overflow of <code>&lt;a&gt;</code> link text in content.</li><li>Restored missing visual-resource path setting.</li><li>Corrected view-count format setting.</li><li>Excluded hidden categories from prev/next navigation.</li><li>Improved mobile-menu tap targets.</li></ul><h3 id="new-features-8" tabindex="-1">New Features <a class="header-anchor" href="#new-features-8" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Rebuilt the bottom-right widget panel.</li><li>Enabled comments on “shuoshuo” entries with count display.</li><li>Switched to a new Gravatar proxy; removed legacy proxies.</li><li>Global ChatGPT toggle added.</li><li>Option to exclude posts from AI summaries.</li><li>Reworked expand/collapse with internationalization.</li><li>Honor <code>&lt;!--more--&gt;</code> tags in post content.</li><li>Enhanced admin-panel UX; replaced hidden settings with masked overlays (<a href="https://github.com/mirai-mamori/Sakurairo/pull/705" target="_blank" rel="noreferrer">demo</a>).</li><li>Auto-update visual-resource paths.</li><li>Custom Gravatar proxy URL.</li><li>Custom inline-code background colors per mode.</li><li>Custom Google Fonts API URL.</li></ul>',99))])}const w=r(h,[["render",u]]);export{k as __pageData,w as default};
